#!venv/bin/python3

input = """"""

buff_letters = []
for i, letter in enumerate(input):
    if len(buff_letters)==4:
            print(f"Found the combination {buff_letters} at offset {i}" )
            break
    else:
        if letter in buff_letters:
            buff_letters = buff_letters[buff_letters.index(letter)+1:] + [letter]     
            continue
        buff_letters.append(letter)
